// URL: https://www.hackerrank.com/challenges/cut-the-sticks

/* */
// Returns an array of integers representing the number 
// of sticks before each cut operation is performed
function cutTheSticks_1($arr) {
    $res = [];
    while($arr) {
        
        // Finds min element of the array to 
        // cut every other elements by it's value
        $min = min($arr);
        
        // The quantity of elements inside 
        // $arr is the answer each while loop
        $res[] = count($arr);
        
        // Loops through the elements left inside $arr
        foreach ($arr as $item) {
            
            // Each element decreases by 
            // the min element of the array
            $item = $item - $min;
            
            // Creates new array with 
            // the elements greater than 0
            if ($item > 0) {
                $arr_n[] = $item;
            }
        } 
        $arr = $arr_n;
        $arr_n = [];
    }
    return $res;
}

// Returns an array of integers representing the number 
// of sticks before each cut operation is performed
// same as the function above but with less loops
function cutTheSticks_2($arr) {
    
    // Sorts the array with ascending order
    sort($arr);
    
    // Counts all elements in an array
    $cnt = count($arr);
    
    // The first number in answer's array is 
    // the initial number of elements of $arr
    $res[] = $cnt;
    
    // Loops through the array elements
    for ($i = 1; $i < $cnt; $i++) {
        
        // If the current element in the sorted 
        // array is not the same as the previous one
        if ($arr[$i] != $arr[$i - 1]) {
            
            // Adds difference to the answer between the
            // quantity of elements and current index (if 
            // the quantity of current element is 3, then $cnt
            // decreased by 3 will be added to the answer's array)
            $res[] = $cnt - $i;
        }
    }
    return $res;
}

